module zero3 (out, in, clk);
	output out;
	input in, clk;
	reg s1, s2, s3;

	assign out = s1==0 & s2==0 & s3==0;

	initial begin
		s1 = 0;
		s2 = 0;
		s3 = 0;
	end

	always @(posedge clk) begin
		s1 <= in;
		s2 <= s1;
		s3 <= s2;
	end
endmodule


# module_top
module top (out, in, clk);
	output out;
	input in, clk;
	reg s1, s2, s3;

	assign out = s1==0 & s2==0 & s3==0;

	initial begin
		s1 = 0;
		s2 = 0;
		s3 = 0;
	end

	always @(posedge clk) begin
		s1 <= in;
		s2 <= s1;
		s3 <= s2;
	end
endmodule

